              <div className="space-y-2">
                <Label htmlFor="force">Force (F)</Label>
                <div className="relative">
                  <Input
                    id="force"
                    type="number"
                    placeholder="Enter force"
                    value={force}
                    onChange={(e) => setForce(e.target.value)}
