              <div className="relative">
                <Input
                  id="r3"
                  data-testid="input-r3"
                  type="number"
                  step="0.1"
                  placeholder="e.g., 2000"
                  value={r3}
                  onChange={(e) => setR3(e.target.value)}
                />
                <span className="absolute right-3 top-1/2 -translate-y-1/2 text-sm text-muted-foreground pointer-events-none">
                  Ω
                </span>
              </div>
            </div>
