import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { CalculatorAccordion } from "@/components/CalculatorAccordion";
import { getCalculatorAccordion } from "@/data/calculatorAccordions";
import { useState } from "react";
import { Breadcrumb } from "@/components/Breadcrumb";

const motorHPKWSchema = z.object({
  value: z.coerce.number().positive("Value must be positive"),
  conversionType: z.enum(["HP-to-kW", "kW-to-HP"]),
});

type MotorHPKWForm = z.infer<typeof motorHPKWSchema>;

interface Results {
  input: number;
  output: number;
  inputUnit: string;
  outputUnit: string;
}

export default function MotorHPKW() {
  const [results, setResults] = useState<Results | null>(null);

  const form = useForm<MotorHPKWForm>({
    resolver: zodResolver(motorHPKWSchema),
    defaultValues: {
      value: undefined,
      conversionType: "HP-to-kW",
    },
  });

  const conversionType = form.watch("conversionType");

  const onSubmit = (data: MotorHPKWForm) => {
    let output: number;
    let inputUnit: string;
    let outputUnit: string;

    if (data.conversionType === "HP-to-kW") {
      // 1 HP = 0.746 kW
      output = data.value * 0.746;
      inputUnit = "HP";
      outputUnit = "kW";
    } else {
      // 1 kW = 1.341 HP
      output = data.value * 1.341;
      inputUnit = "kW";
      outputUnit = "HP";
    }

    setResults({
      input: data.value,
      output,
      inputUnit,
      outputUnit,
    });
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="max-w-4xl mx-auto px-4 py-8">
        <Breadcrumb
          category="Motor"
          calculatorName="Motor HP to kW"
        />

        <div className="mb-8">
          <h1 className="text-4xl font-bold mb-2" data-testid="text-page-title">
            Motor HP to kW Converter
          </h1>
          <p className="text-muted-foreground" data-testid="text-page-description">
            Convert between horsepower (HP) and kilowatts (kW) for motors
          </p>
        </div>

        <div className="grid gap-8 md:grid-cols-1">
          <Card>
            <CardHeader>
              <CardTitle>Conversion Parameters</CardTitle>
              <CardDescription>Enter power value and select conversion direction</CardDescription>
            </CardHeader>
            <CardContent>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="conversionType"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Conversion Type</FormLabel>
                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                          <FormControl>
                            <SelectTrigger data-testid="select-conversion-type">
                              <SelectValue />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="HP-to-kW">HP → kW</SelectItem>
                            <SelectItem value="kW-to-HP">kW → HP</SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="value"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>
                          {conversionType === "HP-to-kW" ? "Power (HP)" : "Power (kW)"}
                        </FormLabel>
                        <FormControl>
                          <Input
                            type="number"
                            step="any"
                            placeholder={conversionType === "HP-to-kW" ? "e.g., 10" : "e.g., 7.46"}
                            data-testid="input-value"
                            value={field.value ?? ""}
                            onChange={(e) => field.onChange(e.target.value === "" ? undefined : e.target.value)}
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <Button type="submit" className="w-full" data-testid="button-convert">
                    Convert
                  </Button>
                </form>
              </Form>
            </CardContent>
          </Card>

          {results && (
            <Card>
              <CardHeader>
                <CardTitle>Results</CardTitle>
                <CardDescription>Power conversion</CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div className="p-4 border rounded-lg">
                    <div className="text-sm text-muted-foreground mb-1">Input Power</div>
                    <div className="text-2xl font-bold font-mono" data-testid="text-input">
                      {results.input.toFixed(3)} {results.inputUnit}
                    </div>
                  </div>

                  <div className="p-4 border rounded-lg bg-primary/5">
                    <div className="text-sm text-muted-foreground mb-1">Output Power</div>
                    <div className="text-2xl font-bold font-mono text-primary" data-testid="text-output">
                      {results.output.toFixed(3)} {results.outputUnit}
                    </div>
                  </div>
                </div>

                <div className="p-4 bg-muted/50 rounded-lg space-y-2">
                  <h4 className="font-semibold">Conversion Formula</h4>
                  {results.inputUnit === "HP" ? (
                    <div className="font-mono text-sm">
                      1 HP = 0.746 kW
                    </div>
                  ) : (
                    <div className="font-mono text-sm">
                      1 kW = 1.341 HP
                    </div>
                  )}
                  <div className="text-xs text-muted-foreground mt-2">
                    Standard mechanical horsepower (HP) to kilowatt (kW) conversion
                  </div>
                </div>

                <div className="p-4 border-l-4 border-l-primary bg-primary/5 rounded">
                  <div className="text-sm font-semibold mb-2">Quick Reference</div>
                  <div className="grid grid-cols-2 gap-2 text-sm">
                    <div>1 HP</div>
                    <div>= 0.746 kW</div>
                    <div>5 HP</div>
                    <div>= 3.73 kW</div>
                    <div>10 HP</div>
                    <div>= 7.46 kW</div>
                    <div>50 HP</div>
                    <div>= 37.3 kW</div>
                    <div>100 HP</div>
                    <div>= 74.6 kW</div>
                  </div>
                </div>
              </CardContent>
            </Card>
          )}
        </div>

        <div className="mt-8">
          <CalculatorAccordion content={getCalculatorAccordion("motor-hp-kw")} />
        </div>
      </div>
    </div>
  );
}

client/src/pages/calculators/ACCurrentDi.content:
import { useState } from "react";
import { Header } from "@/components/Header";
import { Breadcrumb } from "@/components/Breadcrumb";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Button } from "@/components/ui/button";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { AlertCircle } from "lucide-react";
import { CalculatorAccordion } from "@/components/CalculatorAccordion";
import { getCalculatorAccordion } from "@/data/calculatorAccordions";

interface Results {
  current1?: number;
  current2?: number;
  z1Magnitude?: number;
  z2Magnitude?: number;
  currentDivisionRatio?: number;
}

export default function ACCurrentDivider() {
  const [totalCurrent, setTotalCurrent] = useState("");
  const [r1, setR1] = useState("");
  const [x1, setX1] = useState("");
  const [r2, setR2] = useState("");
  const [x2, setX2] = useState("");
  const [results, setResults] = useState<Results | null>(null);
  const [error, setError] = useState("");

  const calculate = () => {
    setError("");
    setResults(null);

    const Itotal = parseFloat(totalCurrent);
    const R1 = parseFloat(r1);
    const X1 = parseFloat(x1);
    const R2 = parseFloat(r2);
    const X2 = parseFloat(x2);

    if (isNaN(Itotal) || isNaN(R1) || isNaN(X1) || isNaN(R2) || isNaN(X2)) {
      setError("Please enter valid numbers for all fields.");
      return;
    }

    if (Itotal <= 0 || R1 < 0 || R2 < 0) {
      setError("Current and resistances must be positive values.");
      return;
    }

    // Calculate impedance magnitudes
    const Z1 = Math.sqrt(R1 * R1 + X1 * X1);
    const Z2 = Math.sqrt(R2 * R2 + X2 * X2);

    if (Z1 === 0 || Z2 === 0) {
      setError("Impedances cannot be zero.");
      return;
    }

    // For parallel impedances, current divides inversely with impedance
    // I1 = Itotal × (Z2 / (Z1 + Z2))
    // I2 = Itotal × (Z1 / (Z1 + Z2))

    const I1 = Itotal * (Z2 / (Z1 + Z2));
    const I2 = Itotal * (Z1 / (Z1 + Z2));

    // Calculate current division ratio
    const ratio = I1 / Itotal;

    setResults({
      current1: I1,
      current2: I2,
      z1Magnitude: Z1,
      z2Magnitude: Z2,
      currentDivisionRatio: ratio,
    });
  };

  const reset = () => {
    setTotalCurrent("");
    setR1("");
    setX1("");
    setR2("");
    setX2("");
    setResults(null);
    setError("");
  };

  return (
    <div className="min-h-screen bg-background">
      <Header />
      <main className="max-w-5xl mx-auto px-4 md:px-6 py-8">
        <Breadcrumb category="AC Circuits" calculatorName="AC Current Divider Calculator" />

        <div className="mb-8">
          <h1 className="text-3xl font-bold mb-2" data-testid="text-calculator-title">AC Current Divider Calculator</h1>
          <p className="text-base text-muted-foreground" data-testid="text-calculator-description">
            Calculate branch currents in parallel AC circuits with complex impedances.
          </p>
        </div>

        <div className="grid md:grid-cols-2 gap-8">
          <Card>
            <CardHeader>
              <CardTitle>Input Values</CardTitle>
              <CardDescription>Enter total current and impedance components</CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="space-y-2">
                <Label htmlFor="totalCurrent">Total Current (Itotal)</Label>
                <div className="relative">
                  <Input
                    id="totalCurrent"
                    type="number"
                    step="any"
                    placeholder="Enter total current"
                    value={totalCurrent}
                    onChange={(e) => setTotalCurrent(e.target.value)}
                    className="pr-12"
                    data-testid="input-current"
                  />
                  <span className="absolute right-3 top-1/2 -translate-y-1/2 text-sm text-muted-foreground">
                    A
                  </span>
                </div>
              </div>

              <div className="p-4 bg-muted/50 rounded-md space-y-4">
                <Label className="text-sm font-medium">Impedance Z1 (Branch 1)</Label>
                <div className="grid grid-cols-2 gap-3">
                  <div className="space-y-2">
                    <Label htmlFor="r1" className="text-xs">Resistance (R1)</Label>
                    <div className="relative">
                      <Input
                        id="r1"
                        type="number"
                        step="any"
                        placeholder="R1"
                        value={r1}
                        onChange={(e) => setR1(e.target.value)}
                        className="pr-8"
                        data-testid="input-r1"
                      />
                      <span className="absolute right-2 top-1/2 -translate-y-1/2 text-xs text-muted-foreground">
                        Ω
                      </span>
                    </div>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="x1" className="text-xs">Reactance (X1)</Label>
                    <div className="relative">
                      <Input
                        id="x1"
                        type="number"
                        step="any"
                        placeholder="X1"
                        value={x1}
                        onChange={(e) => setX1(e.target.value)}
                        className="pr-8"
                        data-testid="input-x1"
                      />
                      <span className="absolute right-2 top-1/2 -translate-y-1/2 text-xs text-muted-foreground">
                        Ω
                      </span>
                    </div>
                  </div>
                </div>
              </div>

              <div className="p-4 bg-muted/50 rounded-md space-y-4">
                <Label className="text-sm font-medium">Impedance Z2 (Branch 2)</Label>
                <div className="grid grid-cols-2 gap-3">
                  <div className="space-y-2">
                    <Label htmlFor="r2" className="text-xs">Resistance (R2)</Label>
                    <div className="relative">
                      <Input
                        id="r2"
                        type="number"
                        step="any"
                        placeholder="R2"
                        value={r2}
                        onChange={(e) => setR2(e.target.value)}
                        className="pr-8"
                        data-testid="input-r2"
                      />
                      <span className="absolute right-2 top-1/2 -translate-y-1/2 text-xs text-muted-foreground">
                        Ω
                      </span>
                    </div>
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="x2" className="text-xs">Reactance (X2)</Label>
                    <div className="relative">
                      <Input
                        id="x2"
                        type="number"
                        step="any"
                        placeholder="X2"
                        value={x2}
                        onChange={(e) => setX2(e.target.value)}
                        className="pr-8"
                        data-testid="input-x2"
                      />
                      <span className="absolute right-2 top-1/2 -translate-y-1/2 text-xs text-muted-foreground">
                        Ω
                      </span>
                    </div>
                  </div>
                </div>
              </div>

              <div className="flex gap-2">
                <Button onClick={calculate} className="flex-1" data-testid="button-calculate">
                  Calculate
                </Button>
                <Button onClick={reset} variant="outline" data-testid="button-reset">
                  Reset
                </Button>
              </div>

              {error && (
                <Alert variant="destructive">
                  <AlertCircle className="h-4 w-4" />
                  <AlertDescription>{error}</AlertDescription>
                </Alert>
              )}
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Results</CardTitle>
              <CardDescription>Branch currents and analysis</CardDescription>
            </CardHeader>
            <CardContent>
              {results ? (
                <div className="space-y-4">
                  <div className="space-y-1">
                    <Label>Current through Z1 (I1)</Label>
                    <div className="p-3 bg-muted rounded-md font-mono text-lg" data-testid="result-current-1">
                      {results.current1!.toFixed(4)} A
                    </div>
                  </div>

                  <div className="space-y-1">
                    <Label>Current through Z2 (I2)</Label>
                    <div className="p-3 bg-muted rounded-md font-mono text-lg" data-testid="result-current-2">
                      {results.current2!.toFixed(4)} A
                    </div>
                  </div>

                  <div className="grid grid-cols-2 gap-3">
                    <div className="space-y-1">
                      <Label className="text-xs">|Z1|</Label>
                      <div className="p-2 bg-muted rounded-md font-mono text-sm" data-testid="result-z1">
                        {results.z1Magnitude!.toFixed(2)} Ω
                      </div>
                    </div>
                    <div className="space-y-1">
                      <Label className="text-xs">|Z2|</Label>
                      <div className="p-2 bg-muted rounded-md font-mono text-sm" data-testid="result-z2">
                        {results.z2Magnitude!.toFixed(2)} Ω
                      </div>
                    </div>
                  </div>

                  <div className="space-y-1">
                    <Label>Current Division Ratio (I1/Itotal)</Label>
                    <div className="p-3 bg-muted rounded-md font-mono text-lg" data-testid="result-ratio">
                      {results.currentDivisionRatio!.toFixed(4)} ({(results.currentDivisionRatio! * 100).toFixed(2)}%)
                    </div>
                  </div>

                  <div className="p-4 bg-primary/5 rounded-md border border-primary/20">
                    <p className="text-sm font-medium mb-2">Formulas:</p>
                    <div className="text-sm text-muted-foreground space-y-1">
                      <p>I1 = Itotal × (|Z2| / (|Z1| + |Z2|))</p>
                      <p>I2 = Itotal × (|Z1| / (|Z1| + |Z2|))</p>
                      <p className="text-xs mt-2">
                        Current divides inversely with impedance in parallel circuits
                      </p>
                    </div>
                  </div>
                </div>
              ) : (
                <div className="text-center text-muted-foreground py-8">
                  Enter values and click Calculate to see results
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {getCalculatorAccordion("ac-current-divider") && (
          <CalculatorAccordion
            content={getCalculatorAccordion("ac-current-divider")!}
            calculatorId="ac-current-divider"
          />
        )}
      </main>
    </div>
  );
}

