import { useState } from "react";
import { Header } from "@/components/Header";
import { Breadcrumb } from "@/components/Breadcrumb";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Button } from "@/components/ui/button";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { AlertCircle, Plus, X } from "lucide-react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { CalculatorAccordion } from "@/components/CalculatorAccordion";
import { getCalculatorAccordion } from "@/data/calculatorAccordions";

export default function SeriesParallelInductor() {
  const [inductors, setInductors] = useState<string[]>(["", ""]);
  const [result, setResult] = useState<number | null>(null);
  const [error, setError] = useState("");
  const [mode, setMode] = useState<"series" | "parallel">("series");

  const addInductor = () => {
    setInductors([...inductors, ""]);
  };

  const removeInductor = (index: number) => {
    if (inductors.length > 2) {
      setInductors(inductors.filter((_, i) => i !== index));
    }
  };

  const updateInductor = (index: number, value: string) => {
    const newInductors = [...inductors];
    newInductors[index] = value;
    setInductors(newInductors);
  };

  const calculate = () => {
    setError("");
    setResult(null);

    const values = inductors.map(l => parseFloat(l)).filter(v => !isNaN(v));

    if (values.length < 2) {
      setError("Please enter at least two inductor values.");
      return;
    }

    if (values.some(v => v <= 0)) {
      setError("All inductance values must be greater than zero.");
      return;
    }

    let totalL: number;

    if (mode === "series") {
      totalL = values.reduce((sum, l) => sum + l, 0);
    } else {
      const reciprocalSum = values.reduce((sum, l) => sum + (1 / l), 0);
      if (reciprocalSum === 0) {
        setError("Invalid calculation result.");
        return;
      }
      totalL = 1 / reciprocalSum;
    }

    setResult(totalL);
  };

  const reset = () => {
    setInductors(["", ""]);
    setResult(null);
    setError("");
  };

  return (
    <div className="min-h-screen bg-background">
      <Header />
      <main className="max-w-4xl mx-auto px-4 md:px-6 py-8">
        <Breadcrumb category="Electrical" calculatorName="Series & Parallel Inductor Calculator" />

        <div className="mb-8">
          <h1 className="text-3xl font-bold mb-2" data-testid="text-calculator-title">Series & Parallel Inductor Calculator</h1>
          <p className="text-base text-muted-foreground" data-testid="text-calculator-description">
            Calculate total inductance for multiple inductors in series (L = L1 + L2 + ...) or parallel (1/L = 1/L1 + 1/L2 + ...)
          </p>
        </div>

        <div className="grid md:grid-cols-2 gap-8">
          <Card>
            <CardHeader>
              <CardTitle>Input Values</CardTitle>
              <CardDescription>Add inductor values</CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              <Tabs value={mode} onValueChange={(v) => setMode(v as "series" | "parallel")}>
                <TabsList className="grid w-full grid-cols-2">
                  <TabsTrigger value="series" data-testid="tab-series">Series</TabsTrigger>
                  <TabsTrigger value="parallel" data-testid="tab-parallel">Parallel</TabsTrigger>
                </TabsList>
              </Tabs>

              <div className="space-y-3 max-h-[400px] overflow-y-auto">
                {inductors.map((l, index) => (
                  <div key={index} className="flex gap-2 items-center">
                    <div className="flex-1 space-y-2">
                      <Label htmlFor={`inductor-${index}`}>Inductor {index + 1}</Label>
                      <div className="relative">
                        <Input
                          id={`inductor-${index}`}
                          type="number"
                          step="any"
                          placeholder={`Enter L${index + 1}`}
                          value={l}
                          onChange={(e) => updateInductor(index, e.target.value)}
                          className="pr-12"
                          data-testid={`input-inductor-${index}`}
                        />
                        <span className="absolute right-3 top-1/2 -translate-y-1/2 text-sm text-muted-foreground">
                          H
                        </span>
                      </div>
                    </div>
                    {inductors.length > 2 && (
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={() => removeInductor(index)}
                        className="mt-7"
                        data-testid={`button-remove-${index}`}
                      >
                        <X className="h-4 w-4" />
                      </Button>
                    )}
                  </div>
                ))}
              </div>

              <Button onClick={addInductor} variant="outline" className="w-full" data-testid="button-add-inductor">
                <Plus className="h-4 w-4 mr-2" />
                Add Inductor
              </Button>

              {error && (
                <Alert variant="destructive">
                  <AlertCircle className="h-4 w-4" />
                  <AlertDescription data-testid="text-error">{error}</AlertDescription>
                </Alert>
              )}

              <div className="flex gap-3">
                <Button onClick={calculate} className="flex-1" data-testid="button-calculate">
                  Calculate
                </Button>
                <Button onClick={reset} variant="outline" data-testid="button-reset">
                  Reset
                </Button>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Results</CardTitle>
              <CardDescription>Total inductance</CardDescription>
            </CardHeader>
            <CardContent>
              {result !== null ? (
                <div className="space-y-6">
                  <div className="p-6 bg-primary/5 rounded-lg border border-primary/20">
                    <div className="text-sm text-muted-foreground mb-2">Total Inductance</div>
                    <div className="text-3xl font-bold font-mono" data-testid="text-result-total">
                      {result.toExponential(4)} H
                    </div>
                  </div>

                  <div className="space-y-3 pt-4 border-t">
                    <h3 className="font-semibold">Formula Used:</h3>
                    <div className="p-4 bg-muted/50 rounded font-mono text-sm">
                      {mode === "series" 
                        ? "L_total = L1 + L2 + L3 + ..." 
                        : "1/L_total = 1/L1 + 1/L2 + 1/L3 + ..."}
                    </div>
                  </div>
                </div>
              ) : (
                <div className="text-center py-12 text-muted-foreground">
                  <p>Enter values and click Calculate to see results</p>
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {getCalculatorAccordion("series-parallel-inductor") && (
          <CalculatorAccordion
            content={getCalculatorAccordion("series-parallel-inductor")!}
            calculatorId="series-parallel-inductor"
          />
        )}
      </main>
    </div>
  );
}

client/src/pages/calculators/Reactance.t.content:
import { useState } from "react";
import { Header } from "@/components/Header";
import { Breadcrumb } from "@/components/Breadcrumb";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Button } from "@/components/ui/button";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { AlertCircle } from "lucide-react";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { CalculatorAccordion } from "@/components/CalculatorAccordion";
import { getCalculatorAccordion } from "@/data/calculatorAccordions";

export default function Reactance() {
  const [frequency, setFrequency] = useState("");
  const [inductance, setInductance] = useState("");
  const [capacitance, setCapacitance] = useState("");
  const [xlResult, setXlResult] = useState<number | null>(null);
  const [xcResult, setXcResult] = useState<number | null>(null);
  const [error, setError] = useState("");
  const [activeTab, setActiveTab] = useState("inductive");

  const calculateInductive = () => {
    setError("");
    setXlResult(null);

    const f = parseFloat(frequency);
    const l = parseFloat(inductance);

    if (isNaN(f) || isNaN(l)) {
      setError("Please enter valid numbers for frequency and inductance.");
      return;
    }

    if (f <= 0 || l <= 0) {
      setError("Frequency and inductance must be greater than zero.");
      return;
    }

    const xl = 2 * Math.PI * f * l;
    setXlResult(xl);
  };

  const calculateCapacitive = () => {
    setError("");
    setXcResult(null);

    const f = parseFloat(frequency);
    const c = parseFloat(capacitance);

    if (isNaN(f) || isNaN(c)) {
      setError("Please enter valid numbers for frequency and capacitance.");
      return;
    }

    if (f <= 0 || c <= 0) {
      setError("Frequency and capacitance must be greater than zero.");
      return;
    }

    const xc = 1 / (2 * Math.PI * f * c);
    setXcResult(xc);
  };

  const reset = () => {
    setFrequency("");
    setInductance("");
    setCapacitance("");
    setXlResult(null);
    setXcResult(null);
    setError("");
  };

  return (
    <div className="min-h-screen bg-background">
      <Header />
      <main className="max-w-4xl mx-auto px-4 md:px-6 py-8">
        <Breadcrumb category="Electrical" calculatorName="Reactance Calculator" />

        <div className="mb-8">
          <h1 className="text-3xl font-bold mb-2" data-testid="text-calculator-title">Reactance Calculator</h1>
          <p className="text-base text-muted-foreground" data-testid="text-calculator-description">
            Calculate inductive reactance (XL = 2πfL) and capacitive reactance (XC = 1/(2πfC))
          </p>
        </div>

        <div className="grid md:grid-cols-2 gap-8">
          <Card>
            <CardHeader>
              <CardTitle>Input Values</CardTitle>
              <CardDescription>Choose reactance type</CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              <div className="space-y-2">
                <Label htmlFor="frequency">Frequency (f)</Label>
                <div className="relative">
                  <Input
                    id="frequency"
                    type="number"
                    step="any"
                    placeholder="Enter frequency"
                    value={frequency}
                    onChange={(e) => setFrequency(e.target.value)}
                    className="pr-12"
                    data-testid="input-frequency"
                  />
                  <span className="absolute right-3 top-1/2 -translate-y-1/2 text-sm text-muted-foreground">
                    Hz
                  </span>
                </div>
                <p className="text-xs text-muted-foreground">
                  Used for both inductive and capacitive reactance
                </p>
              </div>

              <Tabs value={activeTab} onValueChange={setActiveTab}>
                <TabsList className="grid w-full grid-cols-2">
                  <TabsTrigger value="inductive" data-testid="tab-inductive">Inductive (XL)</TabsTrigger>
                  <TabsTrigger value="capacitive" data-testid="tab-capacitive">Capacitive (XC)</TabsTrigger>
                </TabsList>

                <TabsContent value="inductive" className="space-y-4 mt-6">
                  <div className="space-y-2">
                    <Label htmlFor="inductance">Inductance (L)</Label>
                    <div className="relative">
                      <Input
                        id="inductance"
                        type="number"
                        step="any"
                        placeholder="Enter inductance"
                        value={inductance}
                        onChange={(e) => setInductance(e.target.value)}
                        className="pr-12"
                        data-testid="input-inductance"
                      />
                      <span className="absolute right-3 top-1/2 -translate-y-1/2 text-sm text-muted-foreground">
                        H
                      </span>
                    </div>
                  </div>

                  <Button onClick={calculateInductive} className="w-full" data-testid="button-calculate-xl">
                    Calculate Inductive Reactance
                  </Button>
                </TabsContent>

                <TabsContent value="capacitive" className="space-y-4 mt-6">
                  <div className="space-y-2">
                    <Label htmlFor="capacitance">Capacitance (C)</Label>
                    <div className="relative">
                      <Input
                        id="capacitance"
                        type="number"
                        step="any"
                        placeholder="Enter capacitance"
                        value={capacitance}
                        onChange={(e) => setCapacitance(e.target.value)}
                        className="pr-12"
                        data-testid="input-capacitance"
                      />
                      <span className="absolute right-3 top-1/2 -translate-y-1/2 text-sm text-muted-foreground">
                        F
                      </span>
                    </div>
                  </div>

                  <Button onClick={calculateCapacitive} className="w-full" data-testid="button-calculate-xc">
                    Calculate Capacitive Reactance
                  </Button>
                </TabsContent>
              </Tabs>

              {error && (
                <Alert variant="destructive">
                  <AlertCircle className="h-4 w-4" />
                  <AlertDescription data-testid="text-error">{error}</AlertDescription>
                </Alert>
              )}

              <Button onClick={reset} variant="outline" className="w-full" data-testid="button-reset">
                Reset
              </Button>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle>Results</CardTitle>
              <CardDescription>Reactance value</CardDescription>
            </CardHeader>
            <CardContent>
              {(activeTab === "inductive" && xlResult !== null) || (activeTab === "capacitive" && xcResult !== null) ? (
                <div className="space-y-6">
                  <div className="p-6 bg-primary/5 rounded-lg border border-primary/20">
                    <div className="text-sm text-muted-foreground mb-2">
                      {activeTab === "inductive" ? "Inductive Reactance (XL)" : "Capacitive Reactance (XC)"}
                    </div>
                    <div className="text-3xl font-bold font-mono" data-testid="text-result-reactance">
                      {activeTab === "inductive" && xlResult !== null
                        ? `${xlResult.toFixed(4)} Ω` 
                        : activeTab === "capacitive" && xcResult !== null
                        ? `${xcResult.toFixed(4)} Ω`
                        : "No result"}
                    </div>
                  </div>

                  <div className="space-y-3 pt-4 border-t">
                    <h3 className="font-semibold">Formula Used:</h3>
                    <div className="p-4 bg-muted/50 rounded font-mono text-sm">
                      {activeTab === "inductive" 
                        ? "XL = 2πfL" 
                        : "XC = 1 / (2πfC)"}
                    </div>
                    {activeTab === "inductive" && xlResult !== null && (
                      <div className="p-4 bg-muted/50 rounded font-mono text-sm">
                        XL = 2π × {frequency} × {inductance} = {xlResult.toFixed(4)} Ω
                      </div>
                    )}
                    {activeTab === "capacitive" && xcResult !== null && (
                      <div className="p-4 bg-muted/50 rounded font-mono text-sm">
                        XC = 1 / (2π × {frequency} × {capacitance}) = {xcResult.toFixed(4)} Ω
                      </div>
                    )}
                  </div>
                </div>
              ) : (
                <div className="text-center py-12 text-muted-foreground">
                  <p>Enter values and click Calculate to see results</p>
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {getCalculatorAccordion("reactance") && (
          <CalculatorAccordion
            content={getCalculatorAccordion("reactance")!}
            calculatorId="reactance"
          />
        )}
      </main>
    </div>
  );
}

