                      <FormItem>
                        <FormLabel>Capacitance</FormLabel>
                        <FormControl>
                          <Input
                            type="number"
                            step="0.001"
                            placeholder="100"
                            data-testid="input-capacitance"
                            {...field}
                            value={field.value ?? ""}
                          />
